mapper.update:
UPDATE TB_UP_TEMPFILE
SET CHUNK_POS = #{chunkPosition},
    MOD_DTM = NOW()
WHERE FILE_ID = #{fileID}
  AND REG_ID = #{registrationID}

mapper.select:
SELECT FILE_ID,
       FILE_PATH,
       CHUNK_SIZE,
       CHUNK_COUNT,
       CHUNK_POS,
       REG_ID
FROM TB_UP_TEMPFILE
WHERE ORGIN_FILE_NM = #{originalFileName}
  AND ORGIN_FILE_SIZE = #{originalFileSize}
  AND REG_ID = #{registrationID}
